INSERT INTO `pertanyaan` (`id`, `pid`, `nama`, `code`, `jawaban`, `file`) VALUES
	(1, NULL, 'TATA CARA LAYANAN ONLINE ADMINDUK', '1', 'Pelayanan online Dispendukcapil Kabupaten Nganjuk dapat diakses melalui beberapa website kami. Apabila belum memiliki\nakun dapat mendaftarkan diri terlebih dahulu dengan syarat berdomisili asli warga Kabupaten Nganjuk.\n\n*Silahkan Pilih Menu sebagai berikut :*', NULL),
	(2, NULL, 'SYARAT UMUM PENGAJUAN ADMINDUK', '2', 'Berikut syarat umum pengajuan Administrasi Kependudukan Dispendukcapil Kabupaten Nganjuk.\n*Silahkan Piih Menu sebagai berikut :*', NULL),
	(4, 1, 'Cara mengurus Pengajuan Permohonan melalui Online', 'A', '*Halo Sobat Dukcapil....*\n\nPengajuan bisa dilakukan secara online melalui situs kami link: https://dispendukcapil.Kabupaten Nganjuk.go.id/\n\n\n*Apakah Anda ingin mengajukan Permohonan melaui situs disdukcapil nganjuk https://dispendukcapil.Kabupaten Nganjuk.go.id/?*\nJika YA ketik *YA* untuk mengajukan Permohonan.\nJika Tidak ketik *Tidak* Akhiri Percakapan.\nJika ingin kembali ke MENU UTAMA silakan ketik *Menu*', NULL),
	(5, 1, 'Aduan permasalahan masyarakat terkait ADMINDUK', 'B', 'Aduan permasalahan masyarakat *Administrasi Kependudukan*\n*Silahkan Piih Menu sebagai berikut :*', NULL),
	(9, NULL, 'LINK LAYANAN ADMINDUK', '3', 'LINK Layanan *Administrasi Kependudukan*\n*Silahkan Piih Menu sebagai berikut :*', NULL),
	(10, 5, 'Website SAPA WARGA', '1.b', '*Halo Sobat Dukcapil...*\n\n*Website Sapa Warga* merupakan layanan pengaduan untuk mengadukan permasalahan masyarakat terkait administrasi kependudukan.\n\n*Layanan Sapa Warga :*\n- Klik link berikut ini : *https://dukcapilsapawarga-dispendukcapil.Kabupaten Nganjuk.go.id* \n- Silahkan Isi Captcha dengan benar\n- Mohon masukan pengaduan cukup satu kali saja untuk permasalahan yang sama dan jelaskan permasalahan secara detail.\n- Jika ada pengaduan berkali-kali dengan permasalahan yang sama akan memperlama proses solusi permasalahan anda. \n\nNote :  Untuk melihat status pengaduan anda silahkan Klik Menu _*"Cek Pengaduan"*_\n\n\n*Apakah Anda ingin mengajukan Permohonan melaui situs https://dispendukcapil.Kabupaten Nganjuk.go.id?*\nJika YA ketik *YA* untuk mengajukan Permohonan.\nJika Tidak ketik *Tidak* Akhiri Percakapan.\nJika ingin kembali ke MENU UTAMA silakan ketik *Menu*', NULL),
	(11, 5, 'Instagram', '2.b', '*Halo Sobat Dukcapil...*\n\n*Berikut Akun Instagram dan Tata Cara Pengaduan Permasalahan Dispendukcapil Kabupaten Nganjuk:*\n\n@Dispendukcapil.sby atau pada link *https://www.instagram.com/dispendukcapil.sby/*\n- Silahkan Follow akun Instagram @Dispendukcapil.sby \n- Silahkan baca pada Feed IG kami untuk mengetahui perkembangan Informasi Layanan Adminduk di Dispendukcapil Kabupaten Nganjuk\n- Aduan permohonan bisa dilakukan melalui DM IG @Dispendukcapil.sby dengan menyertakan _*"Nama dan NIK Pelapor/Pemohon"*_\n\n\n*Apakah Anda ingin mengajukan Permohonan melaui situs Disdukcapil Nganjuk?*\nJika YA ketik *YA* untuk mengajukan Permohonan.\nJika Tidak ketik *Tidak* Akhiri Percakapan.\nJika ingin kembali ke MENU UTAMA silakan ketik *Menu*', NULL),
	(12, 5, 'Twitter', '3.b', '*Halo Sobat Dukcapil...*\n\n*Berikut Akun Twitter dan Tata Cara Pengaduan Permasalahan Dispendukcapil Kabupaten Nganjuk:*\n\n@DispendukcapilS atau pada link *https://twitter.com/DispendukcapilS/*\n- Silahkan follow Akun Twitter @DispendukcapilS\n- Silahkan membaca informasi pada akun Twitter untuk mengetahui Perkembangan Informasi Layanan Adminduk di Dispendukcapil Kabupaten Nganjuk.\n- Untuk pengajuan terkait aduan permohnan dapat dilakukan melalui DM pada akun Twitter @DispendukcapilS dengan menyertakan _*"Nama dan NIK Pelapor / Pemohon."*_\n\n\n*Apakah Anda ingin mengajukan Permohonan melaui situs Disdukcapil Nganjuk?*\nJika YA ketik *YA* untuk mengajukan Permohonan.\nJika Tidak ketik *Tidak* Akhiri Percakapan.\nJika ingin kembali ke MENU UTAMA silakan ketik *Menu*', NULL),
	(13, 5, 'SSW e-KIOS LAYANAN PUBLIK', '4.b', '*Halo Sobat Dukcapil...*\n\n*Berikut Link SSW e-KIOS LAYANAN PUBLIK dan Tata Cara Pengaduan Permasalahan Dispendukcapil Kabupaten Nganjuk:*\n\nhttps://ssw.Kabupaten Nganjuk.go.id/anjungan/index.php\n- Silahkan masuk pada link *https://ssw.Kabupaten Nganjuk.go.id/anjungan/index.php*\n- Silahkan pilih pada menu Mall Pelayanan Publik ( Jika ingin mengetahui atau mengecek data terkait dokumen Adminduk yang ada).\n\n\n*Apakah Anda ingin mengajukan Permohonan melaui situs https://dispendukcapil.Kabupaten Nganjuk.go.id?*\nJika YA ketik *YA* untuk mengajukan Permohonan.\nJika Tidak ketik *Tidak* Akhiri Percakapan.\nJika ingin kembali ke MENU UTAMA silakan ketik *Menu*', NULL),
	(14, 5, 'E-mail', '5.b', '*Halo Sobat Dukcapil...*\n\n*Berikut Alamat Email dan Tata Cara Pengaduan Permasalahan Dispendukcapil Kabupaten Nganjuk:*\n\ndis_dukcapil@Kabupaten Nganjuk.go.id\nEmail Dispendukcapil Kabupaten Nganjuk merupakan media yang digunakan untuk sarana pengaduan warga melalui by email, reset password akun di https://dispendukcapil.Kabupaten Nganjuk.go.id yang terkait dengan Administrasi Kependudukan.\n\n\n*Apakah Anda ingin mengajukan Permohonan melaui situs https://dispendukcapil.Kabupaten Nganjuk.go.id*\nJika YA ketik *YA* untuk mengajukan Permohonan.\nJika Tidak ketik *Tidak* Akhiri Percakapan.\nJika ingin kembali ke MENU UTAMA silakan ketik *Menu*', NULL),
	(15, 1, 'Cara Mengetahui (Tracking) Proses Pengajuan Dokumen Permohonan Administrasi kependudukan', 'C', 'Silahkan Download Aplikasi TAKON disdukcapil_nganjuk di PlayStore (Khusus Android) atau Pilih Menu AKUN TAKON  di bawah ini https://dispendukcapil.Kabupaten Nganjuk.go.id :', NULL),
	(16, 15, 'AKUN TAKON DISDUKCAPIL NGANJUK', '1.c', '*Halo Sobat Dukcapil...*\n\n*Aplikasi Takon Disdukcapil Nganjuk* digunakan untuk melakukan cek berkala (tracking) permohonan pengajuan dokumen administrasi kependudukan atau bisa dilihat melalui Notifikasi di Akun Disdukcapil Nganjuk.\n\nTata cara tracking dokumen Takon Disdukcapil Nganjuk :\n- Silahkan unduh aplikasi Takon Disdukcapil Nganjuk di Playstore atau pada link berikut : https://play.google.com/store/apps/details?id=dispendukcapil.Kabupaten Nganjuk.takonDisdukcapil Nganjuk&amp;hl=in&amp;gl=US\n- Masukkan NIK/No.E-Kitir anda dan tekan tombol Search\n\n\n*Apakah Anda ingin mengajukan Permohonan melaui situs Disdukcapil Nganjuk?*\nJika YA ketik *YA* untuk mengajukan Permohonan.\nJika Tidak ketik *Tidak* Akhiri Percakapan.\nJika ingin kembali ke MENU UTAMA silakan ketik *Menu*', NULL),
	(17, 1, 'Warga yang berumur 17 tahun atau yang belum pernah perekaman KTP-EL (?)', 'D', '*Halo Sobat Dukcapil...*\n\n*Perekaman Warga yang berumur 17 tahun* atau yang belum pernah perekaman KTP-EL, dapat dilakukan di Kecamatan\n\n- Jika di Kecamatan cukup membawa fotocopy KK (Kartu Keluarga).\n- Jika di Siola dapat dilakukan, dengan:\n- Silakan Ambil Antrean secara Online di:\n*https://ssw.Kabupaten Nganjuk.go.id/anjungan/index.php*\n- Pilih Antrian e-KTP SIOLA, Klik LAYANAN REKAM dan pilih tanggal perekaman.\n\n\n*Apakah Anda ingin mengajukan Permohonan melaui situs Disdukcapil Nganjuk?*\nJika YA ketik *YA* untuk mengajukan Permohonan.\nJika Tidak ketik *Tidak* Akhiri Percakapan.\nJika ingin kembali ke MENU UTAMA silakan ketik *Menu*', NULL),
	(18, 1, 'Proses Pengajuan Permohonan Dokumen Administrasi Kependudukan', 'E', '*Halo Sobat Dukcapil...*\n\n*Proses Pengajuan Permohonan Dokumen Administrasi Kependudukan*\n\n- Waktu Permohonan Administrasi Kependudukan yaitu 7 (Tujuh) Hari Kerja dihitung setelah mendapatkan bukti *E-Kitir.*\n- Jika melebihi waktu yang sudah ditentukan silahkan menghubungi *Call Center* kami di 031-99254200.\n\n\n*Apakah Anda ingin mengajukan Permohonan melaui situs Disdukcapil Nganjuk?*\nJika YA ketik *YA* untuk mengajukan Permohonan.\nJika Tidak ketik *Tidak* Akhiri Percakapan.\nJika ingin kembali ke MENU UTAMA silakan ketik *Menu*', NULL),
	(19, 1, 'Pengambilan Dokumen Administrasi Kependudukan', 'F', '*Halo Sobat Dukcapil...*\n\n*Pengambilan Dokumen Administrasi Kependudukan* khusus untuk *KTP* (Kartu Tanda Penduduk) dan *KIA* (Kartu Identitas Anak) dapat  dilakukan di Kelurahan sesuai dengan domisili / wilayah masing-masing.\n\n\n*Apakah Anda ingin mengajukan Permohonan melaui situs Disdukcapil Nganjuk?*\nJika YA ketik *YA* untuk mengajukan Permohonan.\nJika Tidak ketik *Tidak* Akhiri Percakapan.\nJika ingin kembali ke MENU UTAMA silakan ketik *Menu*', NULL),
	(20, 1, 'Lokasi dan Waktu Layanan Administrasi Kependudukan Dispendukcapil Kabupaten Nganjuk ( Mall Pelayanan Publik Dispendukcapil Kabupaten Nganjuk )', 'G', '*Halo Sobat Dukcapil...*\n\n*Lokasi dan Waktu Layanan Administrasi Kependudukan Dispendukcapil Kabupaten Nganjuk (Mall Pelayanan Publik Dispendukcapil Kabupaten Nganjuk).*\n\n*- Lokasi* Mall Pelayanan Publik Dinas Kependudukan dan Pencatatan Sipil Kabupaten Nganjuk \n*- Jam Operasional Layanan Adminduk* pada hari kerja Senin - Kamis (07.30-16.00), hari Jumat (07.30-15.00) dan hari Sabtu (08.00-13.00).\n\n\n*Apakah Anda ingin mengajukan Permohonan melaui situs Disdukcapil Nganjuk?*\nJika YA ketik *YA* untuk mengajukan Permohonan.\nJika Tidak ketik *Tidak* Akhiri Percakapan.\nJika ingin kembali ke MENU UTAMA silakan ketik *Menu*', NULL),
	(21, 1, 'Tata Cara legalisir dokumen yang belum menggunakan QR-Code', 'H', '*Halo Sobat Dukcapil...*\n\n*Tata Cara legalisir dokumen yang belum menggunakan QR-Code*\n\nDokumen Kependudukan yang *Belum ber-barcode*  terbitan Dispendukcapil Kabupaten Nganjuk, bisa dilakukan melalui website Disdukcapil Nganjuk \n- Fotocopy dokumen kependudukan warna hitam dan putih\n- Dokumen Kependudukan (Asli) yang akan dilegalisir\nNote : untuk dokumen kependudukan bukan terbitan dispendukcapil Kabupaten Nganjuk, legalisirnya di Pengadilan Negeri.\n\n\n*Apakah Anda ingin mengajukan Permohonan melaui situs Disdukcapil Nganjuk?*\nJika YA ketik *YA* untuk mengajukan Permohonan.\nJika Tidak ketik *Tidak* Akhiri Percakapan.\nJika ingin kembali ke MENU UTAMA silakan ketik *Menu*', NULL),
	(22, 1, 'Tata Cara mencetak Dokumen jika sudah diterbitkan', 'I', '*Halo Sobat Dukcapil...*\n\n*Tata Cara mencetak Dokumen Administrasi Kependudukan jika sudah diterbitkan melalui situs Online Disdukcapil Nganjuk*\n\nTata Cara mencetak Dokumen Administrasi Kependudukan *jika sudah diterbitkan* melalui situs Online Disdukcapil Nganjuk\n- Sesuai aturan Permendagri no 109 thn 2019, dokumen kependudukan dicetak secara mandiri di kertas HVS A4 ukuran 80 gram.\n- Dokumen hasil permohonan akan diberikan dalam bentuk PDF yang dapat diunduh oleh Pemohon dan dicetak secara mandiri.\nTerima Kasih.\n\n\n*Apakah Anda ingin mengajukan Permohonan melaui situs Disdukcapil Nganjuk?*\nJika YA ketik *YA* untuk mengajukan Permohonan.\nJika Tidak ketik *Tidak* Akhiri Percakapan.\nJika ingin kembali ke MENU UTAMA silakan ketik *Menu*', NULL),
	(23, 9, 'Disdukcapil Nganjuk', '3.1', '*Halo Sobat Dukcapil..*\n\n*Website Disdukcapil Nganjuk* merupakan layanan pengajuan permohonan administrasi kependudukan bagi warga yang ingin mengurus administrasi kependudukan berbasis online.\n\n*Berikut Link Disdukcapil Nganjuk:*\nhttps://dispendukcapil.Kabupaten Nganjuk.go.id/ \n\n\n*Apakah Anda ingin mengajukan Permohonan melaui situs Disdukcapil Nganjuk?*\nJika YA ketik *YA* untuk mengajukan Permohonan.\nJika Tidak ketik *Tidak* Akhiri Percakapan.\nJika ingin kembali ke MENU UTAMA silakan ketik *Menu*', NULL),
	(24, 9, 'Takon Disdukcapil Nganjuk', '3.2', '*Halo Sobat Dukcapil...*\n\n*Takon Disdukcapil Nganjuk* merupakan sebuah aplikasi layanan dari Dispendukcapil Kabupaten Nganjuk dengan memiliki layanan untuk mengajukan pertanyaan terkait dengan administrasi Adminduk yang sedang diajukan oleh pemohon, sehingga terkait segala permasalahan dapat dilakukan dengan menananyakannya pada aplikasi yang berbasis online ini.\n\nBerikut Link Unduh Aplikasi "Takon Disdukcapil Nganjuk":\nhttps://play.google.com/store/apps/details?id=dispendukcapil.Kabupaten Nganjuk.takonDisdukcapil Nganjuk&amp;hl=in&amp;gl=US\n\n\n*Apakah Anda ingin mengajukan Permohonan melaui situs Disdukcapil Nganjuk?*\nJika YA ketik *YA* untuk mengajukan Permohonan.\nJika Tidak ketik *Tidak* Akhiri Percakapan.\nJika ingin kembali ke MENU UTAMA silakan ketik *Menu*', NULL),
	(25, 9, 'Website Sapa Warga', '3.3', '*Halo Sobat Dukcapil...*\n\n*Website Sapa Warga* adalah website resmi dari Dispendukcapil Kabupaten Nganjuk bagi warga yang memiliki kendala dalam pengurusan dokumen administrasi kependudukan dan melakukan pelaporan terkait permasalahan dan kendala yang dialami.\nhttps://dukcapilsapawarga-dispendukcapil.Kabupaten Nganjuk.go.id/\n\n\n*Apakah Anda ingin mengajukan Permohonan melaui situs Disdukcapil Nganjuk?*\nJika YA ketik *YA* untuk mengajukan Permohonan.\nJika Tidak ketik *Tidak* Akhiri Percakapan.\nJika ingin kembali ke MENU UTAMA silakan ketik *Menu*', NULL),
	(26, 9, 'Link Instagram', '3.4', '*Halo Sobat Dukcapil...*\n\nInstagram @Dispendukcapil.sby merupakan akun sosial media yang dimiliki oleh Dispendukcapil Kabupaten Nganjuk untuk mampu memberikan segala informasi terkait layanan yang ada pada Dispendukcapil Kabupaten Nganjuk berbasis sosial media selain terdapat informasi, Sobat Adminduk juga dapat mengajukan pengaduan melalui fitur DM Instagram dengan format ( Nama Lengkap serta melampirkan NIK ).\n\n*Berikut Link Akun Instagram Dispendukcapil Kabupaten Nganjuk:*\nhttps://www.instagram.com/dispendukcapil.sby/\n\n\n*Apakah Anda ingin mengajukan Permohonan melaui situs Disdukcapil Nganjuk?*\nJika YA ketik *YA* untuk mengajukan Permohonan.\nJika Tidak ketik *Tidak* Akhiri Percakapan.\nJika ingin kembali ke MENU UTAMA silakan ketik *Menu*', NULL),
	(27, 9, 'Link Twitter', '3.5', '*Halo Sobat Dukcapil...*\n\nTwitter @DispendukcapilS merupakan akun sosial media twitter yang dimiliki oleh Dispendukcapil Kabupaten Nganjuk untuk mampu memberikan segala informasi terkait layanan yang ada pada Dispendukcapil Kabupaten Nganjuk berbasis sosial media selain terdapat informasi, Sobat Adminduk juga dapat mengajukan pengaduan melalui fitur DM Twitter dengan format (Nama Lengkap serta melampirkan NIK).\n\n*Berikut Link Akun Twitter Dispendukcapil Kabupaten Nganjuk:*\nhttps://twitter.com/DispendukcapilS/\n\n\n*Apakah Anda ingin mengajukan Permohonan melaui situs Disdukcapil Nganjuk?*\nJika YA ketik *YA* untuk mengajukan Permohonan.\nJika Tidak ketik *Tidak* Akhiri Percakapan.\nJika ingin kembali ke MENU UTAMA silakan ketik *Menu*', NULL),
	(28, 9, 'Link SSW e-KIOS LAYANAN PUBLIK', '3.6', '*Halo Sobat Dukcapil...*\n\n*SSW e-KIOS LAYANAN PUBLIK* adalah website untuk melakukan pencetakan dokumen perizinan secara mandiri oleh warga Kabupaten Nganjuk.\n\n*Berikut Link SSW atau Kabupaten Nganjuk Single Window:*\nhttps://ssw.Kabupaten Nganjuk.go.id/anjungan/index.php\n\n\n*Apakah Anda ingin mengajukan Permohonan melaui situs Disdukcapil Nganjuk?*\nJika YA ketik *YA* untuk mengajukan Permohonan.\nJika Tidak ketik *Tidak* Akhiri Percakapan.\nJika ingin kembali ke *Menu Utama* silakan ketik *Menu*', NULL),
	(29, 9, 'E-mail Resmi Dispendukcapil', '3.7', '*Halo Sobat Dukcapil...*\nEmail Dispendukcapil Kabupaten Nganjuk merupakan media yang digunakan untuk sarana pengaduan warga melalui by email, reset password akun Disdukcapil Nganjuk yang terkait dengan Administrasi Kependudukan.\nBerikut alamat *Email* resmi Dispendukcapil Kabupaten Nganjuk: \ndis_dukcapil@Kabupaten Nganjuk.go.id\n\n\n*Apakah Anda ingin mengajukan Permohonan melaui situs Disdukcapil Nganjuk?*\nJika YA ketik *YA* untuk mengajukan Permohonan.\nJika Tidak ketik *Tidak* Akhiri Percakapan.\nJika ingin kembali ke MENU UTAMA silakan ketik *Menu*', NULL),
	(30, NULL, 'JENIS-JENIS LAYANAN ADMINDUK', '4', '*JENIS-JENIS LAYANAN Administrasi Kependudukan*\nSilakan pilih opsi jenis layanan:', NULL),
	(31, 30, 'KTP-eL', '4.1', 'a. Tempat Pelayanan Pembuatan KTP-el Baru dan Syaratnya\nb. Tata Cara Pembuatan KTP-el Baru\nc. Tata Cara Pengajuan Perubahan Elemen Data, Rusak dan Hilang terkait KTP-el\nd. Tata Cara Cetak Pengajuan Perubahan Elemen Data KTP-el\ne. Terkait Legalisir KTP-el\nf. Memeriksa ( Tracking ) permohonan KTP-el\ng. Tata Cara Pembuatan KTP-el luar Kabupaten Nganjuk ( Luar Jawa Timur )\nh. Tata Cara Cetak Pembuatan KTP-el Luar Kabupaten Nganjuk ( Luar Jawa Timur )\ni. Tata Cara Pengajuan Perekaman Ulang KTP-el Akibat Kesalahan Biometrik', NULL),
	(32, 30, 'Akta Kelahiran', '4.2', 'Silakan pilih opsi yang Anda inginkan:\n\n*a. Tata cara Pengajuan Permohonan Akta Kelahiran*\n\n*b. Kepengurusan Akta Kelahiran untuk:*\n   1. Untuk Diri Sendiri\n   2. Untuk Anak atau Cucu (Keluarga satu KK)\n   3. Lain - lain\n   \n   *1. Diri sendiri*\n      a. Tata Cara dan Syarat Pembetulan Nama Akta Kelahiran\n      b. Tata Cara dan Syarat Penerbitan Akta Kelahiran untuk Dewasa\n      c. Tata Cara Mengurus Akta Kelahiran yang Hilang\n      d. Tata Cara Legalisir Akta Kelahiran\n   \n   *2. Anak atau Cucu (Keluarga satu KK)*\n      a. Tata Cara dan Syarat Mengurus Pengajuan Akta Kelahiran\n      b. Tata Cara dan Syarat Pembetulan Nama Akta Kelahiran\n      c. Pengurusan Akta Kelahiran Bayi yang Lahir di Luar Kabupaten Nganjuk\n      d. Tata Cara Mengurus Akta Kelahiran yang Hilang\n      e. Tata Cara Legalisir Akta Kelahiran\n   \n   *3. Lain - Lain*\n      a. Pengurusan Akta Kelahiran tanpa Memiliki Surat Keterangan Lahir dan Keberadaan Orang Tua Tidak Jelas.\n      b. Tata Cara Membuat Akta Kelahiran, jika Orang Tua tidak Memiliki Surat Nikah atau Akta Kawin atau Ibu dibawah Umur\n      c. Tata Cara Penerbitan Akta untuk Anak Adopsi', NULL),
	(33, 30, 'Akta Kematian', '4.3', 'a. Tempat Kepengurusan Akta Kematian\nb. Tata Cara Perubahan nama pada Akta Kematian\nc. Tata Cara Kepengurusan Akta Kematian yang Hilang\nd. Tata Cara Legalisir Akta Kematian\ne. Tata Cara Mengurus Akta Kematian untuk Anggota Keluarga Tunggal ( Tidak memiliki Keluarga / Tunggal di dalam KK )', NULL),
	(34, 30, 'Akta Perkawinan', '4.4', 'a. Tata cara pengajuan pencatatan perkawinan\nb.  Pengajuan Akta Perkawinan untuk :\n1. WNI menikah di Indonesia\n2. WNI menikah di luar negeri\n3. WNI dan WNA\nc. Tata cara pengajuan akta perkawinan \nd. Tata cara pembayaran denda akta perkawinan', NULL),
	(35, 30, 'Akta Perceraian', '4.5', 'a. Tata cara pengajuan akta perceraian\nb. Pengajuan Akta Perceraian untuk :\n  1. Diri Sendiri;\n  2. Keluarga;\n  3. Lain – lain\n\n  1. Diri Sendiri \n      - Persyaratan membuat akta perceraian\n      - Kelengkapan berkas pengajun akta perceraian\n      - Tata cara mendapat no akta perceraian \n      - Tata cara mencetak akta perceraian secara online\n      - Tata cara legalisir akta perceraian \n  2. Keluarga \n      - Tata cara dan syarat mengurus akta perceraian\n      - Kelengkapan berkas pengajuan akta perceraian\n      - Tata cara mendapat no akta perceraian yang hilang\n      - Tata cara pengambilan akta perceraian salah satu pemohon sudah meninggal\n      - Tata cara legalisisr akta perceraian \n  3. Lain – lain \n      - Tata cara mengurus akta perceraian tanpa pengacara\n      - Tata cara pengecekan status perceraian', NULL),
	(36, 30, 'Pemuktahiran Biodata', '4.6', 'a. Tata cara pengurusan pengajuan permohonan perubahan biodata\nb. Pengajuan permohonan perubahan (Pemutakhiran) biodata untuk :\n    1. Diri Sendiri\n    2. Keluarga', NULL),
	(37, 30, 'Pindah Keluar WNI', '4.7', 'a. Tempat Mengurus Pindah Keluar WNI\nb. Syarat Pengajuan Pindah Keluar\nc. Jika Penduduk Yang Pindah Masih Dibawah 17 Tahun\nd. Persyaratan Batal Pindah\ne. Jika Yang Pindah Adalah Istri Dan Anak (Bercerai)\nf. Durasi Proses Pengajuan Pindah', NULL),
	(38, 30, 'Pindah Datang', '4.8', 'a. Tempat Mengurus Pindah Datang\nb. Syarat Pengajuan Pindah Datang\nc. Tata Cara Jika Penduduk Yang Pindah Masih Dibawah 17 Tahun\nd. Jika Alamat Tujuan Bukan Milik Sendiri\ne. Jika Masa Berlaku Masa SKPWNI Sudah Habis\nf. Durasi Pengurusan Pindah Datang', NULL),
	(39, 30, 'Pindah Dalam Kota', '4.9', 'a. Tempat Mengurus Pindah Dalam Kota\nb. Syarat Pengajuan Pindah Dalam Kota\nc. Tata Cara Proses Pindah Jika Alamat Yang Digunakan Adalah Rusun\nd. Persyaratan Jika Pemohon Tinggal Di Apartemen', NULL),
	(40, 30, 'Kartu Keluarga', '4.10', '*Silakan pilih opsi yang Anda inginkan:*\na. Tata cara Pengajuan Kartu Keluarga\nb. Tata cara pemutakhiran Biodata &amp; Pemutakhiran Biodata Keluarga\nPEMUTAKHIRAN BIODATA KELUARGA :\n*Untuk pemohon usia dibawah 17 thn, gunakan akun orang tua\n*Untuk orang tua yang mau mengubah data anaknya\n*Atau untuk anak yang mau mengubah data keluarganya\nc. Tata cara cetak KK yang rusak\nD. Tata cara cetak KK yang hilang\nE. Tata cara memperbaharui KK WNI yang lama (KK warna merah/KK warna pink/KK bahan karton/KK warna biru muda)\nF. Tata cara pecah Kartu Keluarga \nG. Tata cara mengurus KK setelah pelaporan Akta Lahir Luar Negeri', NULL),
	(41, 4, 'AKUN Disdukcapil Nganjuk', '1.a', '*AKUN Disdukcapil Nganjuk*\nWebsite Disdukcapil Nganjuk digunakan untuk mengajukan permohonan seperti cetak E-KTP, Akta Kelahiran, KK dan sebagainya.\n\nTata cara pembuatan akun Disdukcapil Nganjuk :\n- Silahkan masuk pada link berikut  https://dispendukcapil.Kabupaten Nganjuk.go.id/ \n- Buat akun Disdukcapil Nganjuk\n- Verifikasi akun (Foto KTP-el/KK/Surat kehilangan dari Kepolisian dan Selfie dengan KTP-el/KK dengan ukuran maks.1MB)\n- Tunggu proses verifikasi (1x24 jam), apabila sudah diverifikasi maka dapat lanjut ke tahap berikutnya\n\n*Apakah Anda ingin mengajukan Permohonan melaui situs Disdukcapil Nganjuk?*\nJika YA ketik *YA* untuk mengajukan Permohonan.\nJika Tidak ketik *Tidak* Akhiri Percakapan.\nJika ingin kembali ke MENU UTAMA silakan ketik *Menu*', NULL),
	(46, 2, 'Syarat Umum KTP-eL', '2.1', '*Halo Sobat Dukcapil...*\n\nBerikut syarat umum KTP-eL:\n1. Fotocopy KK\n2. Nomer Antrian (Apabila melakukan perekaman di Siola)\n\n- Perekaman KTP-el dilakukan pada HARI dan JAM KERJA\n- Nomer antrian bisa dicetak atau di screenshoot dan disimpan di gadget masing-masing. Nomor antrian dapat didapatkan pada http://ssw.Kabupaten Nganjuk.go.id/anjungan/index.php\n\n*Apakah Anda ingin mengajukan Permohonan melaui situs Disdukcapil Nganjuk?*\nJika YA ketik *YA* untuk mengajukan Permohonan.\nJika Tidak ketik *Tidak* Akhiri Percakapan.\nJika ingin kembali ke MENU UTAMA silakan ketik *Menu*', NULL),
	(47, NULL, 'Disdukcapil Nganjuk', '5', '*Disdukcapil Nganjuk* merupakan situs Layanan Dispendukcapil Kabupaten Nganjuk yang dapat digunakan untuk mengajukan berbagai permohonan administrasi kependudukan, seperti: Permohonan cetak KTP-el, Permohonan penerbitan akta kelahiran, Permohonan penerbitan Kartu Keluarga, dan lain sebagainya. \nDisdukcapil Nganjuk dapat diakses melalui Android dan Website.\n\nAnda dapat mengunduh aplikasi Disdukcapil Nganjuk melalui Google PlayStore pada Link berikut:\n......\n\nAtau anda dapat mengakses Disdukcapil Nganjuk melalui Link Website:\n*v2.disdukcapilKabupaten Nganjuk.id*', NULL),
	(49, 2, 'Syarat Umum Akta Kelahiran', '2.2', '*Halo Sobat Dukcapil...*\nPengajuan akta kelahiran bisa dilakukan dengan cara sebagai berikut :\n \n1. Pengajuan bisa dilakukan di Kelurahan/Kecamatan \n2. Pengajuan bisa dilakukan secara online melalui situs kami link: https://dispendukcapil.Kabupaten Nganjuk.go.id/ \n\nPERSYARATAN AKTA KELAHIRAN :\n1. Surat Kelahiran ASLI dr dokter/bidan/RS/penolong kelahiran\n2. KK dan KTP-el ortu\n3. fotocopy buku nikah ortu dilegalisir\n4. ktp-el 2 orang saksi (saksi berktp-elektronik)\n\n\n*Apakah Anda ingin mengajukan Permohonan melaui situs Disdukcapil Nganjuk?*\nJika YA ketik *YA* untuk mengajukan Permohonan.\nJika Tidak ketik *Tidak* Akhiri Percakapan.\nJika ingin kembali ke MENU UTAMA silakan ketik *Menu*', NULL),
	(50, 2, 'Syarat Umum Akta Kematian', '2.3', '*Halo Sobat Dukcapil...*\n\nPengajuan Akta Kematian dapat dilakukan secara online (mandiri) melalui website Disdukcapil Nganjuk di dispendukcapil.Kabupaten Nganjuk.go.id atau di kelurahan / kecamatan sesuai domisili.\n\nuntuk persyaratan mengurus akta kematian adalah sebagai berikut:\n1. Surat kematian dari dokter/RS ASLI atau Surat Pernyataan kematian dari keluarga (Bisa didownload pada menu permohonan akta kematian) ditanda tangani di atas materai 10000\n2. KTP/KK Asli yang meninggal\n3. Fotocopy KTP &amp; KK pelapor\n4. Fotocopy KTP 2 orang saksi (saksi KTP-el Kabupaten Nganjuk)\n\n\n*Apakah Anda ingin mengajukan Permohonan melaui situs Disdukcapil Nganjuk?*\nJika YA ketik *YA* untuk mengajukan Permohonan.\nJika Tidak ketik *Tidak* Akhiri Percakapan.\nJika ingin kembali ke MENU UTAMA silakan ketik *Menu*', NULL),
	(51, 2, 'Syarat Umum Akta Perkawinan', '2.4', '*Halo Sobat Dukcapil....*\nPengisian formulir permohonan akta perkawinan bisa melalui https://dispendukcapil.Kabupaten Nganjuk.go.id/ dan melengkapi berkas yang diperlukan.\n\nPersyaratan Pencatatan Perkawinan :\n1. Mengisi formulir permohonan melalui https://dispendukcapil.Kabupaten Nganjuk.go.id/\n2. Surat keterangan telah terjadinya perkawinan dari pemuka agama\n3. Pas foto berdampingan 4x6 (background bebas)\n4. KK &amp; KTP-el (apabila salah satu mempelai warga luar Kabupaten Nganjuk)\n*Catatan: Saksi pencatatan perkawinan mohon bukan orang tua kandung atau kakek atau nenek dari mempelai serta saksi berusia minimal 21 tahun dan memiliki KTP-el Kabupaten Nganjuk\n\n\n*Apakah Anda ingin mengajukan Permohonan melaui situs Disdukcapil Nganjuk?*\nJika YA ketik *YA* untuk mengajukan Permohonan.\nJika Tidak ketik *Tidak* Akhiri Percakapan.\nJika ingin kembali ke MENU UTAMA silakan ketik *Menu*', NULL),
	(52, 2, 'Syarat Umum Akta Perceraian', '2.5', '*Halo Sobat Dukcapil...* \n\nUntuk pengajuan Akta Perceraian dapat dilakukan dengan beberapa cara, sebagai berikut : \n\n1. Pengajuan bisa dilakukan secara offline dengan mengunjungi Kelurahan/Kecamatan yang saat ini menjadi domisili pemohon \n2. Selanjutnya pengajuan secara online dapat dilakukan dengan mengunjungi situs website Disdukcapil Nganjuk, berikut link sistus website dapat di akses : https://dispendukcapil.Kabupaten Nganjuk.go.id \n\nNOTE : jika belum memiliki akun silahkan mendaftar akun dengan memilih menu "DAFTAR" jika sudah memiliki akun silahkan langsung login menggunakan username dan password kemudian memilih menu "PERMOHONAN AKTA PERCERAIAN"\n\n\n*Apakah Anda ingin mengajukan Permohonan melaui situs Disdukcapil Nganjuk?*\nJika YA ketik *YA* untuk mengajukan Permohonan.\nJika Tidak ketik *Tidak* Akhiri Percakapan.\nJika ingin kembali ke MENU UTAMA silakan ketik *Menu*', NULL),
	(53, 2, 'Syarat Umum Pemutakhiran Biodata', '2.6', '*Halo Sobat Dukcapil....* \nPengajuan permohonan pemuktahiran biodata bisa dilakukan dengan cara sebagai berikut : \n\n1. Pengajuan bisa dilakukan di Kelurahan/Kecamatan \n2. Pengajuan bisa dilakukan secara online melalui situs kami link https://dispendukcapil.Kabupaten Nganjuk.go.id/ \n\nNote : jika belum memiliki akun silahkan mendaftar akun dengan memilih menu "DAFTAR" jika sudah memiliki akun silahkan langsung login menggunakan username dan password kemudian memilih menu "PEMUTAKHIRAN BIODATA KELUARGA".\n\n\n*Apakah Anda ingin mengajukan Permohonan melaui situs ?*\nJika YA ketik *YA* untuk mengajukan Permohonan.\nJika Tidak ketik *Tidak* Akhiri Percakapan.\nJika ingin kembali ke MENU UTAMA silakan ketik *Menu*', NULL),
	(54, 2, 'Syarat Umum Pindah Keluar WNI', '2.7', '*Halo Sobat Dukcapil....* \nPengajuan permohonan pindah keluar bisa dilakukan dengan cara sebagai berikut : \n\n- Pemohon hanya perlu melampirkan KK Kabupaten Nganjuk (jika melakukan pengajuan melalui kelurahan / kecamatan maka diserahkan ke petugas)\n(KTP-el hanya ditunjukkan saja)\n- Pengajuan Pindah Keluar dapat dilakukan secara mandiri melalui website Disdukcapil Nganjuk pada alamat dispendukcapil.Kabupaten Nganjuk.go.id atau melalui kelurahan / kecamatan / RT.\n\n\n*Apakah Anda ingin mengajukan Permohonan melaui situs Disdukcapil Nganjuk?*\nJika YA ketik *YA* untuk mengajukan Permohonan.\nJika Tidak ketik *Tidak* Akhiri Percakapan.\nJika ingin kembali ke MENU UTAMA silakan ketik *Menu*', NULL),
	(55, 2, 'Syarat Umum Pindah Datang', '2.8', '*Halo Sobat Dukcapil....* \n\nPengajuan pindah datang bisa dilakukan dengan cara sebagai berikut : \n\n- Surat Keterangan Pindah dari daerah asal (SKPWNI), SPTJM (formulir disediakan di kelurahan).\n- Pengajuan Pindah datang dapat dilakukan di kelurahan / di RT  yang selanjutnya akan dilakukan outreach oleh petugas ke rumah pemohon dan RT.\n\n\n*Apakah Anda ingin mengajukan Permohonan melaui situs Disdukcapil Nganjuk?*\nJika YA ketik *YA* untuk mengajukan Permohonan.\nJika Tidak ketik *Tidak* Akhiri Percakapan.\nJika ingin kembali ke MENU UTAMA silakan ketik *Menu*', NULL),
	(56, 2, 'Syarat Umum Pindah Dalam Kota', '2.9', '*Halo Sobat Dukcapil...*\n\nPengajuan bisa dilakukan dengan cara sebagai berikut : \n- KK Kabupaten Nganjuk, KTP-el, surat jaminan tempat tinggal (disediakan oleh kelurahan), dan Berita Acara Verifikasi (formulir disediakan oleh kelurahan).\n- Pengajuan Pindah Datang dapat dilakukan di kelurahan tujuan yang selanjutnya akan dilakukan outreach oleh petugas ke rumah pemohon dan RT dengan hasil outreach berupa berita acara verifikasi.\n\n\n*Apakah Anda ingin mengajukan Permohonan melaui situs Disdukcapil Nganjuk?*\nJika YA ketik *YA* untuk mengajukan Permohonan.\nJika Tidak ketik *Tidak* Akhiri Percakapan.\nJika ingin kembali ke MENU UTAMA silakan ketik *Menu*', NULL),
	(57, 2, 'Syarat Umum Kartu Keluarga', '2.10', '*Halo Sobat Dukcapil...*\n\nBerikut ini adalah persyaratan untuk pengajuan Kartu Keluarga:\n1. Fotocopy Akta Kawin;\n2. Surat Keterangan Jaminan Tempat Tinggal dari pemilik rumah diketahui oleh RT &amp;RW;\n3. KTP pemilik rumah;\n4. Surat Keterangan Pindah Datang (bila yang pindah dalam wilayah Negara RI);\n5. Surat Keterangan Datang dari Luar Negeri (bagi yang datang dari Luar Negeri karena pindah);\n6. Akta Kelahiran/Biodata (bila ada).\n\n\n*Apakah Anda ingin mengajukan Permohonan melaui situs Disdukcapil Nganjuk?*\nJika YA ketik *YA* untuk mengajukan Permohonan.\nJika Tidak ketik *Tidak* Akhiri Percakapan.\nJika ingin kembali ke MENU UTAMA silakan ketik *Menu*', NULL),
	(63, NULL, 'PERTANYAAN UMUM TERKAIT ADMINDUK', '6', 'Silakan pilih opsi *Pertanyaan Umum* terkait ADMINDUK:', NULL),
	(64, 63, 'Bagaimana cara memperbaiki data jika ada yang salah?', '6.1', '*Halo Sobat Dukcapil...*\n\nJika mengalami kendala dapat mengajukan melalui Petugas Dispendukcapil yang bertugas di Kantor Kecamatan sesuai domisili KK Saudara, atau bisa menghubungi Call Center kami di 031-99254200.  \n\n\n*Apakah Anda ingin mengajukan Permohonan melaui situs Disdukcapil Nganjuk?*\nJika YA ketik *YA* untuk mengajukan Permohonan.\nJika Tidak ketik *Tidak* Akhiri Percakapan.\nJika ingin kembali ke MENU UTAMA silakan ketik *Menu*', NULL),
	(65, 1, 'Selesai', 'Selesai', 'Mudah Bukan Sobat Dukcapil... Ayo Segera Tertib Administrasi Kependudukan.\n\n\n*Apakah Anda ingin mengajukan Permohonan melaui situs Disdukcapil Nganjuk?*\n\nJika YA ketik *YA* untuk mengajukan Permohonan.\nJika Tidak ketik *TIdak* Akhiri Percakapan.', NULL),
	(67, 31, 'a. Tempat Pelayanan Pembuatan KTP-el Baru dan Syaratnya', '4.1a', '*a. Tempat Pelayanan Pembuatan KTP-eL Baru dan Syaratnya:*\n\n*Halo Sobat Dukcapil...*\nUntuk perekaman KTP-eL yang baru berumur 17 tahun bisa dilakukan di seluruh kantor kecamatan di Kabupaten Nganjuk atau Mall Pelayanan Publik\n\n*Berikut persyaratan melakukan perekaman KTP-eL , yaitu:*\n1. Fotocopy KK\n2. Nomer Antrian (Apabila melakukan perekaman di Siola)\n\n*Perekaman KTP-el dilakukan pada HARI dan JAM KERJA\n\n\n*Apakah Anda ingin mengajukan Permohonan melaui situs Disdukcapil Nganjuk?*\nJika YA ketik *YA* untuk mengajukan Permohonan.\nJika Tidak ketik *Tidak* Akhiri Percakapan.\nJika ingin kembali ke MENU UTAMA silakan ketik *Menu*', NULL),
	(70, 31, 'b. Tata Cara Pembuatan KTP-el Baru', '4.1b', '*b. Tata Cara Pembuatan KTP-eL Baru*\n\n*Halo Sobat Dukcapil...*                                                                                                                                                                                                                                                                                                                                               Silahkan Anda konfirmasi kepada pihak kecamatan, untuk melakukan pelayanan perekaman KTP-eL baru. Apabila, kecamatan Anda menerima perekaman KTP-eL, silakan melakukan perekaman KTP-eL di kecamatan, namun apabila tidak menerima perekaman KTP-eL baru silakan Anda melakukan perekaman manual dengan mengambil nomer antrian melalui http://ssw.Kabupaten Nganjuk.go.id/anjungan/index.php\n(silakan cek secara berkala untuk info terbaru tanggal pendaftaran perekaman)\n\nPersyaratan melakukan perekaman ktp-el , yaitu :\n1. Fotocopy KK\n2. Nomer Antrian (Apabila melakukan perekaman)\n\n*Perekaman KTP-el dilakukan pada HARI &amp; JAM KERJA\n*Nomer antrian bisa dicetak atau di screenshoot dan disimpan di gadget masing-masing.\n\n\n*Apakah Anda ingin mengajukan Permohonan melaui situs Disdukcapil Nganjuk?*\nJika YA ketik *YA* untuk mengajukan Permohonan.\nJika Tidak ketik *Tidak* Akhiri Percakapan.\nJika ingin kembali ke MENU UTAMA silakan ketik *Menu*', NULL),
	(73, 32, 'a. Tata cara Pengajuan Akta Kelahiran', '4.2a', '*a. Tata cara Pengajuan Permohonan Akta Kelahiran*\n\n*Halo Sobat Dukcapil...*\nPengajuan bisa dilakukan dengan cara sebagai berikut :\n1. Pengajuan bisa dilakukan di Kelurahan/Kecamatan \n2. Pengajuan bisa dilakukan secara online melalui situs kami link:  https://dispendukcapil.Kabupaten Nganjuk.go.id/\n\nNOTE: Jika belum memiliki akun silahkan mendaftar akun dengan memilih menu _*DAFTAR*_  jika sudah memiliki akun silahkan langsung login menggunakan username dan password kemudian memilih menu _*PERMOHONAN AKTA KELAHIRAN*_.\n\n\n*Apakah Anda ingin mengajukan Permohonan melaui situs Disdukcapil Nganjuk?*\nJika YA ketik *YA* untuk mengajukan Permohonan.\nJika Tidak ketik *Tidak* Akhiri Percakapan.\nJika ingin kembali ke MENU UTAMA silakan ketik *Menu*', NULL),
	(76, 32, 'b. Kepengurusan Akta Kelahiran', '4.2b', '*Silakan pilih opsi Kepengurusan Akta Kelahiran, berikut:*\n\n1. Untuk Diri Sendiri\n2. Untuk Anak atau Cucu (Keluarga satu KK)\n3. Lain - lain', NULL),
	(77, 76, '1. Akta Kelahiran untuk Diri Sendiri', '4.2b1', '*1. Pengurusan Akta Kelahiran untuk Diri Sendiri*\n\nPilih opsi pertanyaan berikut:\na. Persyaratan pembetulan Nama Akta Kelahiran \nb. Tata Cara dan Syarat Penerbitan Akta Kelahiran untuk Dewasa \nc. Tata Cara Mengurus Akta Kelahiran yang Hilang \nd. Tata Cara Legalisir Akta Kelahiran', NULL),
	(78, 77, 'a. Pembetulan Nama pada Akta Kelahiran', '4.2b1a', '*a. Persyaratan pembetulan Nama pada Akta Kelahiran*\n\n*Halo Sobat Dukcapil...*\nNama pada Akta Kelahiran *dapat dibetulkan*, dengan mengajukan Perubahan Nama pada Akta Kelahiran secara online (mandiri) melalui website di https://dispendukcapil.Kabupaten Nganjuk.go.id/ atau di kelurahan / kecamatan sesuai domisili dengan melampirkan persyaratan.\n\nPersyaratan Permohonan Perubahan Nama:\n1. Asli dan fotocopy Kutipan Akta Kelahiran yang akan mengalami perubahan nama\n2. Fotocopy KTP + KK \n3. Fotocopy akta perkawinan / nikah (bagi yang sudah menikah)\n4. Fotocopy salinan Penetapan Pengadilan Negeri tentang Ganti Nama yang dilegalisasi\n\n\n*Apakah Anda ingin mengajukan Permohonan melaui situs Disdukcapil Nganjuk?*\nJika YA ketik *YA* untuk mengajukan Permohonan.\nJika Tidak ketik *Tidak* Akhiri Percakapan.\nJika ingin kembali ke MENU UTAMA silakan ketik *Menu*', NULL),
	(80, 77, 'b. Akta Kelahiran untuk Dewasa (Bayi Tua)', '4.2b1b', '*b. Tata Cara dan Syarat Penerbitan Akta Kelahiran untuk Dewasa*\n\n*Halo Sobat Dukcapil...*\nAkta Kelahiran *dapat* diterbitkan untuk *orang dewasa*. \n\nPengajuan Akta Kelahiran dapat dilakukan secara online (mandiri) melalui website di  https://dispendukcapil.Kabupaten Nganjuk.go.id/ atau di kelurahan / kecamatan sesuai domisili dengan melampirkan:\n- Surat Kelahiran ASLI (apabila ada) atau SPTJM KELAHIRAN, \n- KK dan KTP-el pemohon, \n- fotocopy buku nikah milik orang tua pemohon dilegalisir (apabila ada) atau SPTJM KEBENARAN SUAMI-ISTRI pengganti buku nikah orang tua, \n- ktp-el 2 orang saksi (saksi berktp-elektronik).\n\n*SPTJM KELAHIRAN dan SPTJM KEBENARAN SUAMI-ISTRI dapat diunduh melalui website.\n\n\n*Apakah Anda ingin mengajukan Permohonan melaui situs Disdukcapil Nganjuk?*\nJika YA ketik *YA* untuk mengajukan Permohonan.\nJika Tidak ketik *Tidak* Akhiri Percakapan.\nJika ingin kembali ke MENU UTAMA silakan ketik *Menu*', NULL),
	(81, 77, 'c. Akta Kelahiran yang Hilang', '4.2b1c', '*c. Tata Cara pengurusan Akta Kelahiran yang Hilang*\n\n*Halo Sobat Dukcapil...*\nPengajuan Kutipan Kedua Akta Kelahiran karena hilang *dapat* dilakukan secara online (mandiri) melalui website Disdukcapil Nganjuk di  https://dispendukcapil.Kabupaten Nganjuk.go.id/ atau di kelurahan / kecamatan dengan melampirkan surat kehilangan dari kepolisian dan fotokopi akta kelahiran yang hilang.  \n\nNOTE: Jika tidak memiliki fotokopi akta kelahiran yang hilang, maka pemohon diarahkan untuk mengajukan konfirmasi akta terlebih dahulu melalui menu konfirmasi keabsahan akta melalui website Disdukcapil Nganjuk. \n\n\n*Apakah Anda ingin mengajukan Permohonan melaui situs Disdukcapil Nganjuk?*\nJika YA ketik *YA* untuk mengajukan Permohonan.\nJika Tidak ketik *Tidak* Akhiri Percakapan.\nJika ingin kembali ke MENU UTAMA silakan ketik *Menu*', NULL),
	(82, 33, 'a. Tempat Kepengurusan Akta Kematian', '4.3a', '*Halo Sobat Dukcapil...*\n\nPengajuan Akta Kematian dapat dilakukan secara online (mandiri) melalui website Disdukcapil Nganjuk di dispendukcapil.Kabupaten Nganjuk.go.id atau di kelurahan / kecamatan sesuai domisili. \n\n\n*Apakah Anda ingin mengajukan Permohonan melaui situs Disdukcapil Nganjuk?*\nJika YA ketik *YA* untuk mengajukan Permohonan.\nJika Tidak ketik *Tidak* Akhiri Percakapan.\nJika ingin kembali ke MENU UTAMA silakan ketik *Menu*', NULL),
	(83, 33, 'b. Tata Cara Perubahan nama pada Akta Kematian', '4.3b', '*Halo Sobat Dukcapil...*\nUntuk perubahan nama pada akta kematian dapat dilakukan dengan cara mengajukan Perubahan Nama pada Akta Kematian  dapat dilakukan secara online (mandiri) melalui website Disdukcapil Nganjuk di dispendukcapil.Kabupaten Nganjuk.go.id atau di kelurahan / kecamatan sesuai domisili dengan melampirkan persyaratan penetapan pengadilan negeri.\n\n\n*Apakah Anda ingin mengajukan Permohonan melaui situs Disdukcapil Nganjuk?*\nJika YA ketik *YA* untuk mengajukan Permohonan.\nJika Tidak ketik *Tidak* Akhiri Percakapan.\nJika ingin kembali ke MENU UTAMA silakan ketik *Menu*', NULL),
	(84, 33, 'c. Tata Cara Kepengurusan Akta Kematian yang Hilang', '4.3c', '*Halo Sobat Dukcapil...*\n\nPengajuan Kutipan Kedua Akta Kematian  karena hilang dapat dilakukan secara online (mandiri) atau di kelurahan / kecamatan dengan melampirkan surat kehilangan  dari kepolisian dan fotokopi akta kematian yang hilang.  \n\n\n*Apakah Anda ingin mengajukan Permohonan melaui situs Disdukcapil Nganjuk?*\nJika YA ketik *YA* untuk mengajukan Permohonan.\nJika Tidak ketik *Tidak* Akhiri Percakapan.\nJika ingin kembali ke MENU UTAMA silakan ketik *Menu*', NULL),
	(85, 77, 'd. Tata Cara Legalisir Akta Kelahiran', '4.2b1d', '*d. Tata Cara Legalisir Akta Kelahiran*\n\n*Halo Sobat Dukcapil...*\nJika akta kelahiran yang *belum bertandatangan* elektronik, *dapat dilegalisir* secara online (mandiri) melalui website  di  https://dispendukcapil.Kabupaten Nganjuk.go.id/ atau di kelurahan / kecamatan sesuai domisili.\n\nPengajuan Legalisir Akta Kelahiran dapat lakukan dengan cara berikut:\n- Buat akun di website Disdukcapil Nganjuk\n- Verifikasi akun (upload foto KTP-el/KK dan selfie dengan KTP-el/KK)\n- Tunggu proses verifikasi (1x24 jam), apabila sudah diverifikasi maka dapat lanjut ke tahap berikutnya\n- Pilih menu Legalisir Online\n- Isi data dengan benar dan lengkap\n\n*Untuk contoh posisi scan dokumen bisa di liat melaui tombol Posisi Dokumen yang discan, setelah memilih dokumen yang akan discan\n\nNOTE: Sesuai Peraturan Menteri Dalam Negeri Republik Indonesia No. 104 Tahun 2019 pasal 19 ayat (6) bahwa dokumen kependudukan dengan format digital dan sudah ditandatangani secara elektronik dan KTP-el tidak memerlukan legalisir.\n\n\n*Apakah Anda ingin mengajukan Permohonan melalui situs Disdukcapil Nganjuk?*\nJika YA ketik *YA* untuk mengajukan Permohonan.\nJika Tidak ketik *Tidak* Akhiri Percakapan.\nJika ingin kembali ke MENU UTAMA silakan ketik *Menu*', NULL),
	(86, 33, 'd. Tata Cara Legalisir Akta Kematian', '4.3d', '*Halo Sobat Dukcapil...*\nJika akta kematian yang _*belum bertandatangan elektronik*_, dapat dilegalisir secara online (mandiri) melalui website  pada alamat  dispendukcapil.Kabupaten Nganjuk.go.id atau di kelurahan / kecamatan sesuai domisili. \nSesuai Peraturan Menteri Dalam Negeri Republik Indonesia No. 104 Tahun 2019 pasal 19 ayat (6) bahwa dokumen kependudukan dengan format digital dan sudah ditandatangani secara elektronik dan KTP-el tidak memerlukan legalisir. \n\n\n*Apakah Anda ingin mengajukan Permohonan melaui situs Disdukcapil Nganjuk?*\nJika YA ketik *YA* untuk mengajukan Permohonan.\nJika Tidak ketik *Tidak* Akhiri Percakapan.\nJika ingin kembali ke MENU UTAMA silakan ketik *Menu*', NULL),
	(87, 33, 'e. Tata Cara Mengurus Akta Kematian untuk Anggota Keluarga Tunggal ( Tidak memiliki Keluarga / Tunggal di dalam KK )', '4.3e', '*Halo Sobat Dukcapil...*\nJika yang meninggal tidak memiliki keluarga / tunggal di dalam kartu keluarga. Maka, Pelapornya adalah Ketua RT dengan melampirkan SK pengangkatan RT dan persyaratan lain sesuai ketentuan.\n\n\n*Apakah Anda ingin mengajukan Permohonan melaui situs Disdukcapil Nganjuk?*\nJika YA ketik *YA* untuk mengajukan Permohonan.\nJika Tidak ketik *Tidak* Akhiri Percakapan.\nJika ingin kembali ke MENU UTAMA silakan ketik *Menu*', NULL),
	(88, 31, 'c. Tata Cara Pengajuan Perubahan Elemen Data, Rusak dan Hilang terkait KTP-el', '4.1c', '*Halo Sobat Dukcapil...*\nTerkait pengajuan cetak ulang KTP-el yang mengalami kerusakan, perubahan data, atau hilang dapat dilakukan secara online (mandiri)  atau dapat dilakukan di kelurahan / kecamatan dengan melampirkan: \n-Surat Kehilangan\n-Kartu Keluarga terbaru\n-KTP-el lama untuk kemudian dicetak oleh Dinas Kependudukan dan Pencatatan Sipil dan dikirim ke kelurahan.\n\n\n*Apakah Anda ingin mengajukan Permohonan melaui situs Disdukcapil Nganjuk?*\nJika YA ketik *YA* untuk mengajukan Permohonan.\nJika Tidak ketik *Tidak* Akhiri Percakapan.\nJika ingin kembali ke MENU UTAMA silakan ketik *Menu*', NULL),
	(89, 31, 'd. Tata Cara Cetak Pengajuan Perubahan Elemen Data KTP-el', '4.1d', '*Halo Sobat Dukcapil...*\nTerkait pengajuan KTP-el karena perubahan element pada KTP-el bisa dilakukan melalui situs kami https://dispendukcapil.Kabupaten Nganjuk.go.id/ dengan mengikuti arahan sebagai berikut:\n- Pilih menu LOGIN WARGA\n- Buat akun \n- Verifikasi akun (Foto KK dan Selfie dengan KK)\nTunggu proses verifikasi (1x24 jam), apabila sudah diverifikasi maka dapat lanjut ke tahap berikutnya\n- Pilih menu Cetak KTP-el (Identitasku)\n- Login kembali dgn username dan password \n- Pilih menu di pojok kiri atas\n- Pilih permohonan pengajuan baru\n- Cek dan isi data pemohon dgn benar dan lengkap \n- Pilih alasan “Perubahan element”\n- Upload foto KTP-el lama dan KK Kabupaten Nganjuk baru\n- Cek di menu status pengajuan. Tunggu sampai permohonan ditolak/diapprove oleh petugas. Note: Apabila permohonan diapproval petugas, pemohon akan mendapatkan E-KITIR. Apabila permohonan ditolak, maka mohon dipahami catatan dr petugas utk alasan penolakannya \nPengambilan KTP-el di kelurahan dgn membawa ekitir dan KTP-el lama , silakan tracking secara berkala melalui aplikasi TAKON Disdukcapil Nganjuk (download di playstore). Apabila KTP-el sudah jadi dan dapat diambil di kelurahan , akan muncul keterangan di aplikasi tersebut.\n\n\n*Apakah Anda ingin mengajukan Permohonan melaui situs Disdukcapil Nganjuk?*\nJika YA ketik *YA* untuk mengajukan Permohonan.\nJika Tidak ketik *Tidak* Akhiri Percakapan.\nJika ingin kembali ke MENU UTAMA silakan ketik *Menu*', NULL),
	(90, 31, 'e. Terkait Legalisir KTP-el', '4.1e', '*Halo Sobat Dukcapil...*\nMengikuti Peraturan Menteri Dalam Negeri Republik Indonesia No. 104 Tahun 2019 pasal 19 ayat (6) bahwa dokumen kependudukan dengan format digital dan sudah ditandatangani secara elektronik dan KTP-el tidak memerlukan legalisir. \n\n\n*Apakah Anda ingin mengajukan Permohonan melaui situs Disdukcapil Nganjuk?*\nJika YA ketik *YA* untuk mengajukan Permohonan.\nJika Tidak ketik *Tidak* Akhiri Percakapan.\nJika ingin kembali ke MENU UTAMA silakan ketik *Menu*', NULL),
	(91, 31, 'f. Memeriksa ( Tracking ) permohonan KTP-el', '4.1f', '*Halo Sobat Dukcapil...*\nTerkair status pengajuan cetak KTP-el dapat dilihat pada aplikasi android Takon Disdukcapil Nganjuk pada menu Layanan Takon-ID, lalu Cek Status Kependudukan, KTP-el. \n\n\n*Apakah Anda ingin mengajukan Permohonan melaui situs Disdukcapil Nganjuk?*\nJika YA ketik *YA* untuk mengajukan Permohonan.\nJika Tidak ketik *Tidak* Akhiri Percakapan.\nJika ingin kembali ke MENU UTAMA silakan ketik *Menu*', NULL),
	(92, 31, 'g. Tata Cara Pembuatan KTP-el luar Kabupaten Nganjuk ( Luar Jawa Timur )', '4.1g', '*Halo Sobat Dukcapil...*\n\nTerkait perekaman KTP-el Penduduk luar domisili Kabupaten Nganjuk (Luar Jawa Timur), bisa pendaftaran melalui petugas Dispendukcapil yang ada di Kecamatan. Dengan membawa Fotocopy KK Pemohon. Untuk pelaksanaan perekaman KTP-el dilakukan di Kantor Pelayanan Dispendukcapil  (alamat bisa Pemohon cari melalui google), sesuai jadwal yang diinfokan oleh Petugas. Namun untuk cetak KTP-el, yang mencetak adalah Dispendukcapil sesuai domisili KK Pemohon. \n\n\n*Apakah Anda ingin mengajukan Permohonan melaui situs Disdukcapil Nganjuk?*\nJika YA ketik *YA* untuk mengajukan Permohonan.\nJika Tidak ketik *Tidak* Akhiri Percakapan.\nJika ingin kembali ke MENU UTAMA silakan ketik *Menu*', NULL),
	(93, 31, 'h. Tata Cara Cetak Pembuatan KTP-el Luar Kabupaten Nganjuk ( Luar Jawa Timur )', '4.1h', '*Halo Sobat Dukcapil...*\n\nBerikut persyaratan untuk cetak KTP-el Luar Kabupaten Nganjuk (Luar Jawa Timur) sebagai berikut :\n- KTP-el asli \n- Fotocopy KK\n- Surat permohonan untuk cetak KTP-el luar Kabupaten Nganjuk yg ditujukan kepada kepala Dinas Kependudukan dan Pencatatan Sipil Kabupaten Nganjuk\npengajuan cetak ktp-el luar Kabupaten Nganjuk silahkan kakak ke Kecamatan sesuai domisili saat ini untuk bertemu dengan petugas Dispendukcapil yang ditempatkan di Kecamatan. \nTerima Kasih\n\n\n*Apakah Anda ingin mengajukan Permohonan melaui situs Disdukcapil Nganjuk?*\nJika YA ketik *YA* untuk mengajukan Permohonan.\nJika Tidak ketik *Tidak* Akhiri Percakapan.\nJika ingin kembali ke MENU UTAMA silakan ketik *Menu*', NULL),
	(94, 31, 'i. Tata Cara Pengajuan Perekaman Ulang KTP-el Akibat Kesalahan Biometrik', '4.1i', '*Halo Sobat Dukcapil...*\n\nTerkait perekaman ulang KTP-el dikarenakan YOB (salah background/menggunakan peci/memakai softlens/memakai kacamata/foto buram/tanda tangan kosong) pengajuan bisa dilakukan di Kecamatan sesuai domisili KK bertemu dengan petugas Dispendukcapil yang ditempatkan di Kecamatan. \nBisa disampaikan ke petugas untuk pengajuan Perekaman YOB dan didaftarkan melalui e-sulay. \n\nUntuk perekaman KTP-el ulang diwajibkan untuk mengantri ya sobat Adminduk, jadi sobat Adminduk tidak perlu datang ke kantor pelayanan kami apabila belum dihubungi petugas. Note: mohon untuk tidak mengganti nomor HP agar memudahkan petugas untuk menghubungi kakak.\nTerimakasih\n\n\n*Apakah Anda ingin mengajukan Permohonan melaui situs Disdukcapil Nganjuk?*\nJika YA ketik *YA* untuk mengajukan Permohonan.\nJika Tidak ketik *Tidak* Akhiri Percakapan.\nJika ingin kembali ke MENU UTAMA silakan ketik *Menu*', NULL),
	(95, 76, '2. Akta Kelahiran untuk Anak/Cucu', '4.2b2', '*2. Pengurusan Akta Kelahiran untuk Anak/Cucu (Keluarga satu KK)*\n\nPilih opsi pertanyaan berikut:\na. Tata Cara dan Syarat Mengurus Pengajuan Akta Kelahiran \nb. Tata Cara dan Syarat Pembetulan Nama Akta Kelahiran \nc. Tata Cara Pengurusan Akta Kelahiran Bayi yang Lahir di Luar Kabupaten Nganjuk \nd. Tata Cara Mengurus Akta Kelahiran yang Hilang \ne. Tata Cara Legalisir Akta Kelahiran', NULL),
	(96, 63, 'Berapa Lama waktu Pengajuan ADMINDUK?', '6.2', '*Halo Sobat Dukcapil...*\nWaktu Permohonan Administrasi Kependudukan yaitu 7 (Tujuh) Hari Kerja dihitung setelah mendapatkan bukti E-Kitir. \n\n\n*Apakah Anda ingin mengajukan Permohonan melaui situs Disdukcapil Nganjuk?*\nJika YA ketik *YA* untuk mengajukan Permohonan.\nJika Tidak ketik *Tidak* Akhiri Percakapan.\nJika ingin kembali ke MENU UTAMA silakan ketik *Menu*', NULL),
	(98, 63, 'Bagaimana cara mencetak Dokumen ADMINDUK?', '6.4', '*Halo Sobat Dukcapil...*\nUntuk Mencetak Dokumen Adminduk Jika sudah *diterbitkan melalui online mandiri Disdukcapil Nganjuk*, *Sesuai aturan Permendagri no 109 thn 2019*, dokumen kependudukan dicetak secara mandiri di kertas *HVS A4 ukuran 80 gram*.\n\nDokumen hasil permohonan akan diberikan dalam bentuk PDF yang dapat diunduh oleh Pemohon dan dicetak secara mandiri. \n\n\n*Apakah Anda ingin mengajukan Permohonan melaui situs Disdukcapil Nganjuk?*\nJika YA ketik *YA* untuk mengajukan Permohonan.\nJika Tidak ketik *Tidak* Akhiri Percakapan.\nJika ingin kembali ke MENU UTAMA silakan ketik *Menu*', NULL),
	(99, 63, 'Bagaimana Jika NIK tidak bisa terbaca oleh sistem?', '6.3', '*Halo Sobat Dukcapil...*\nBagi Sobat Adminduk Kabupaten Nganjuk yang mengalami permasalahan  kependudukan dalam pelayanan publik seperti registrasi provider, BPJS, Pembukaan Rekening Bank dan pengurusan sejenisnya. Silahkan *KONSOLIDASI* NIK melalui *DUKCAPIL SAPAWARGA.*\n\nAkses Link dibawah ini :\nhttps://dukcapilsapawarga-dispendukcapil.Kabupaten Nganjuk.go.id\nDengan melampirkan data dibawah ini :\n1. NIK :\n2. NO.KK :\n3. NAMA :\n4. EMAIL :\n5. PERMASALAHAN :\n\n\n*Apakah Anda ingin mengajukan Permohonan melaui situs Disdukcapil Nganjuk?*\nJika YA ketik *YA* untuk mengajukan Permohonan.\nJika Tidak ketik *Tidak* Akhiri Percakapan.\nJika ingin kembali ke MENU UTAMA silakan ketik *Menu*', NULL),
	(100, 63, 'Verifikasi akun Disdukcap butuh waktu berapa lama?', '6.5', 'Verifikasi Akun Disdukcapil Nganjuk menunggu 2x24 jam, dan dilakukan pada hari kerja.\n\n\n*Apakah Anda ingin mengajukan Permohonan melaui situs Disdukcapil Nganjuk?*\nJika YA ketik *YA* untuk mengajukan Permohonan.\nJika Tidak ketik *Tidak* Akhiri Percakapan.\nJika ingin kembali ke MENU UTAMA silakan ketik *Menu*', NULL),
	(101, 63, 'Mengapa NIK dan KK tidak valid menu Disdukcapil?', '6.6', '*Halo Sobat Dukcapil...*\n\nKetika dinyatakan tidak valid saat pengajuan proses pada menu-menu di Disdukcapil Nganjuk, NIK yang didaftarkan di akun harus sama dengan NIK yang mengajukan permohonan Kepengurusan.\n\n\n*Apakah Anda ingin mengajukan Permohonan melaui situs Disdukcapil Nganjuk?*\nJika YA ketik *YA* untuk mengajukan Permohonan.\nJika Tidak ketik *Tidak* Akhiri Percakapan.\nJika ingin kembali ke MENU UTAMA silakan ketik *Menu*', NULL),
	(102, 63, 'Mengapa tidak bisa upload dokumen persyaratan?', '6.7', '*Halo Sobat Dukcapil...*\n\nApabila tidak bisa upload dokumen persyaratan pada menu-menu di Disdukcapil Nganjuk, Pastikan ukuran dokumen tidak lebih dari 250kb dan perhatikan format dokumen yang diminta apakah JPEG atau PDF.\n\n\n*Apakah Anda ingin mengajukan Permohonan melaui situs Disdukcapil Nganjuk?*\nJika YA ketik *YA* untuk mengajukan Permohonan.\nJika Tidak ketik *Tidak* Akhiri Percakapan.\nJika ingin kembali ke MENU UTAMA silakan ketik *Menu*', NULL),
	(103, 34, 'a. Tata cara pengajuan pencatatan perkawinan', '4.4.a', '*a. Tata cara pengajuan pencatatan perkawinan*\n\n*Halo Sobat Dukcapil...*\nPengajuan pencatatan perkawinan dapat dilakukan dengan pengisian formulir permohonan melalui https://dispendukcapil.Kabupaten Nganjuk.go.id/ dan melengkapi berkas yang diperlukan. Persyaratan Pencatatan Perkawinan :\n1. Mengisi formulir permohonan melalui https://dispendukcapil.Kabupaten Nganjuk.go.id/\n2. Surat keterangan telah terjadinya perkawinan dari pemuka agama\n3. Pas foto berdampingan 4x6 (background bebas)\n4. KK &amp; KTP-el (apabila salah satu mempelai warga luar Kabupaten Nganjuk)\n*saksi pencatatan perkawinan mohon bukan orang tua kandung atau kakek atau nenek dari mempelai serta saksi berusia minimal 21 thn dan memiliki KTP-el Kabupaten Nganjuk\n\n\n*Apakah Anda ingin mengajukan Permohonan melaui situs Disdukcapil Nganjuk?*\nJika YA ketik *YA* untuk mengajukan Permohonan.\nJika Tidak ketik *Tidak* Akhiri Percakapan.\nJika ingin kembali ke MENU UTAMA silakan ketik *Menu*', NULL),
	(104, 34, 'b. Pengajuan Akta Perkawinan', '4.4.b', '*Silakan pilih opsi pengajuan kepengurusan Akta Perkawinan, berikut:*\n1. WNI menikah di Indonesia \n2. WNI menikah di luar negeri \n3. WNI dan WNA', NULL),
	(105, 104, '1.  WNI menikah di Indonesia', '4.4.b1', '*1.  WNI menikah di Indonesia*\n\n*Halo Sobat Dukcapil...*\nAnda dapat melakukan pengisian formulir permohonan melalui https://dispendukcapil.Kabupaten Nganjuk.go.id/ dan melengkapi berkas yang diperlukan. Persyaratan Pencatatan Perkawinan :\n1. Mengisi formulir permohonan melalui https://dispendukcapil.Kabupaten Nganjuk.go.id/\n2. Surat keterangan telah terjadinya perkawinan dari pemuka agama\n3. Pas foto berdampingan 4x6 (background bebas)\n4. KK &amp; KTP-el (apabila salah satu mempelai warga luar Kabupaten Nganjuk)\n*saksi pencatatan perkawinan mohon bukan orang tua kandung atau kakek atau nenek dari mempelai serta saksi berusia minimal 21 thn dan memiliki KTP-el Kabupaten Nganjuk\n\n\n*Apakah Anda ingin mengajukan Permohonan melaui situs Disdukcapil Nganjuk?*\nJika YA ketik *YA* untuk mengajukan Permohonan.\nJika Tidak ketik *Tidak* Akhiri Percakapan.\nJika ingin kembali ke MENU UTAMA silakan ketik *Menu*', NULL),
	(106, 104, '2. WNI menikah di luar negeri', '4.4.b2', '*2. WNI menikah di luar negeri*\n\n*Halo Sobat Dukcapil...*\nBerdasarkan Undang-Undang No 23 tahun 2006 tentang Kependudukan Pasal 37 ayat 4 bahwa :\nPenduduk Kabupaten Nganjuk yang beragama non muslim yang sudah melaksanakan perkawinan dihadapan pemuka agama atau penghayat kepercayaan di luar negeri wajib untuk melakukan pelaporan kepada Dispendukcapil paling lambat 30 hari sejak yang bersangkutan kembali ke daerah.\nPersyaratan perkawinan untuk pernikahan WNI di luar negeri yakni : \n1. Bukti pencatatan perkawinan/akta perkawinan dari negara setempat yang sudah diterjemahkan kedalam bahasa Indonesia\n2. Akta Kelahiran\n3. Paspor Republik Indonesia\n4. KTP dan KK\n\n\n*Apakah Anda ingin mengajukan Permohonan melaui situs Disdukcapil Nganjuk?*\nJika YA ketik *YA* untuk mengajukan Permohonan.\nJika Tidak ketik *Tidak* Akhiri Percakapan.\nJika ingin kembali ke MENU UTAMA silakan ketik *Menu*', NULL),
	(107, 104, '3. WNI dan WNA', '4.4.b3', '*3. WNI dan WNA*\n\n*Halo Sobat Dukcapil...*\nPERSYARATAN :\n1. Fotocopy Kutipan Akta Kelahiran / Surat Keterangan Kelahiran suami istri dengan menunjukkan aslinya (catatan pinggir bagi yang sudah berganti nama dan atau berubah kewarganegaraan);\n2. Surat Bukti Pemberkatan Perkawinan menurut agamanya;\na. Fotocopy dengan menunjukkan aslinya bila pemberkatan dan pencatatan dilakukan bersamaan;\nb. Fotocopy dilegalisir bila pemberkatan dan pencatatan tidak dilakukan bersamaan.\n3. Fotocopy KK dan KTP suami istri dengan menunjukkan aslinya;\n4. Fotocopy Surat bukti ganti nama (bagi yang telah ganti nama) dengan menunjukkan aslinya;\n5. Fotocopy Dokumen Imigrasi, STLD dari Kepolisian dan surat dari Kedutaan/Konsul/Perwakilan Negaranya (bagi Orang Asing yang akan melakukan perkawinan dengan WNI) dengan menunjukkan aslinya;\n6. Surat-surat kelengkapan dari Kedutaan Besar yang bersangkutan (bagi perkawinan antar Orang Asing) asli;\n7. Fotocopy Kutipan Akta Kelahiran Anak (bagi calon mempelai yang telah mempunyai anak yang akan diakui dan disahkan setelah perkawinan dengan menunjukkan aslinya;\n8. Izin tertulis dari orang tua apabila belum mencapai umur 21 (dua puluh satu) tahun (asli);\n9. Izin tertulis dari Pengadilan Negeri apabila calon suami belum mencapai umur 19 (sembilan belas) tahun dan calon isteri belum mencapai umur 16 (enam belas) tahun;\n10. Izin dari pejabat yang berwenang bagi anggota TNI/POLRI (asli);\n11. Kutipan Akta Perceraian bagi yang cerai hidup (asli);\n12. Pas Foto berdampingan ukuran 4 x 6 cm sebanyak 5 lembar;\n13. Fotocopy KTP 2 (dua) orang saksi (yang mengetahui peristiwa);\n14. Surat Pernyataan Belum Pernah Menikah dengan materai Rp 6.000,-;\n15. Surat Keterangan dari Kelurahan setempat.\nRETRIBUSI :\n\nWNI : Rp. 275.000,-\nOrang Asing : Rp. 500.000,-\n\n\n*Apakah Anda ingin mengajukan Permohonan melaui situs Disdukcapil Nganjuk?*\nJika YA ketik *YA* untuk mengajukan Permohonan.\nJika Tidak ketik *Tidak* Akhiri Percakapan.\nJika ingin kembali ke MENU UTAMA silakan ketik *Menu*', NULL),
	(108, 34, 'c. Tata cara pengajuan akta perkawinan', '4.4.c', '*c. Tata cara pengajuan akta perkawinan*\n\n*Halo Sobat Dukcapil...*\nPendaftaran akta perkawinan dilakukan oleh calon mempelai yang merupakan penduduk Kabupaten Nganjuk. Permohonan dapat dilakukan secara online melalui situs kami https://dispendukcapil.Kabupaten Nganjuk.go.id/ . Langkah-langkah yang dilakukan yakni : \n- Pilih login warga\n- Daftar akun \n- Verifikasi akun (Foto KTP-el/KK dan Selfie dengan KTP-el/KK)\n- Tunggu proses verifikasi (1x24 jam), apabila sudah diverifikasi maka dapat lanjut ke tahap berikutnya\n- Pilih menu Permohonan Akta Perkawinan \n- Isi data dengan benar dan lengkap \n- Jangan lupa upload berkas persyaratannya ya (untuk berkas persyaratannya dijadikan 1 file PDF)\n- Di akhir permohonan, Pemohon akan mendapat e-kitir sebagai tanda bukti permohonan\n\n\n*Apakah Anda ingin mengajukan Permohonan melaui situs Disdukcapil Nganjuk?*\nJika YA ketik *YA* untuk mengajukan Permohonan.\nJika Tidak ketik *Tidak* Akhiri Percakapan.\nJika ingin kembali ke MENU UTAMA silakan ketik *Menu*', NULL),
	(109, 34, 'd. Tata cara pembayaran denda akta perkawinan', '4.4.d', '*d. Tata cara pembayaran denda akta perkawinan*\n\n*Halo Sobat Dukcapil...*\nUntuk pembayaran denda akan diarahkan oleh petugas sesudah mengajukan pendaftaran melalui Disdukcapil Nganjuk. Pembayaran disarankan melalui teller bank jatim agar mendapat bukti pembayaran\n\n\n*Apakah Anda ingin mengajukan Permohonan melaui situs Disdukcapil Nganjuk?*\nJika YA ketik *YA* untuk mengajukan Permohonan.\nJika Tidak ketik *Tidak* Akhiri Percakapan.\nJika ingin kembali ke MENU UTAMA silakan ketik *Menu*', NULL),
	(110, 36, 'a. Tata cara permohonan perubahan biodata', '4.6.a', '*a. Tata cara pengurusan pengajuan permohonan perubahan biodata*\n\n*Halo Sobat Dukcapil...*\nPengajuan bisa dilakukan dengan cara sebagai berikut :\n1. Pengajuan bisa dilakukan di Kelurahan/Kecamatan \n2. Pengajuan bisa dilakukan secara online melalui situs kami link (https://dispendukcapil.Kabupaten Nganjuk.go.id/)\n\nNote : jika belum memiliki akun silahkan mendaftar akun dengan memilih menu "DAFTAR"  jika sudah memiliki akun silahkan langsung login menggunakan username dan password kemudian memilih menu "PEMUTAKHIRAN BIODATA KELUARGA".\n\n\n*Apakah Anda ingin mengajukan Permohonan melaui situs Disdukcapil Nganjuk?*\nJika YA ketik *YA* untuk mengajukan Permohonan.\nJika Tidak ketik *Tidak* Akhiri Percakapan.\nJika ingin kembali ke MENU UTAMA silakan ketik *Menu*', NULL),
	(111, 36, 'b. Pengajuan perubahan (Pemutakhiran) biodata', '4.6.b', '*Silahkan pilih opsi permohonan perubahan (Pemutakhiran) biodata, berikut :*\n1. Untuk Diri Sendiri\n2. Untuk Keluarga', NULL),
	(112, 111, '1. Untuk Diri Sendiri', '4.6.b1', '*1. Untuk Diri Sendiri*\n\n*Halo Sobat Dukcapil...*\nSilahkan melakukan pengajuan secara online melalui situs kami Disdukcapil Nganjuk, syarat perubahan biodata bisa disesuaikan dengan pengajuan permohonan di menu "PEMUTAKHIRAN BIODATA".\ncontoh : Merubah Data Status Perkawinan (Belum Kawin/Kawin/Cerai Hidup/Cerai Mati).\nsyaratnya : Melampirkan Dokumen Kartu Keluarga; Dokumen Perkawinan \n\nNote : Untuk Syarat Permohonan, bisa disesuaikan dengan Status Biodata yang ingin di rubah.me\n\n\n*Apakah Anda ingin mengajukan Permohonan melaui situs Disdukcapil Nganjuk?*\nJika YA ketik *YA* untuk mengajukan Permohonan.\nJika Tidak ketik *Tidak* Akhiri Percakapan.\nJika ingin kembali ke MENU UTAMA silakan ketik *Menu*', NULL),
	(113, 111, '2. Untuk Keluarga', '4.6.b2', '*2. Untuk Keluarga*\n\n*Halo Sobat Dukcapil...*\nSilahkan melakukan pengajuan secara online melalui situs kami Disdukcapil Nganjuk, syarat perubahan biodata bisa disesuaikan dengan pengajuan permohonan di menu PEMUTAKHIRAN BIODATA KELUARGA, mengisi kolom Nama Ayah/Ibu sesuai yang diinginkan dan melampirkan berkas persyaratan berupa KK dan Akta Kelahiran.\nJika mengalami kendala dapat mengajukan melalui Petugas Dispendukcapil yang bertugas di Kantor Kecamatan sesuai domisili KK Saudara, terimakasih.\n\n\n*Apakah Anda ingin mengajukan Permohonan melaui situs Disdukcapil Nganjuk?*\nJika YA ketik *YA* untuk mengajukan Permohonan.\nJika Tidak ketik *Tidak* Akhiri Percakapan.\nJika ingin kembali ke MENU UTAMA silakan ketik *Menu*', NULL),
	(114, 35, 'a. Tata Cara Pengajuan Akta Perceraian', '4.5.a', '*a.Tata Cara Pengajuan Akta Perceraian*\n\n*Halo Sobat Dukcapil...*\nUntuk pengajuan Akta Perceraian dapat dilakukan dengan beberapa cara, sebagai berikut : \n1. Pengajuan bisa dilakukan secara offline dengan mengunjungi Kelurahan/Kecamatan yang saat ini menjadi domisili pemohon;\n2. Selanjutnya pengajuan secara online dapat dilakukan dengan mengunjungi situs website Disdukcapil Nganjuk, berikut link sistus website dapat di akses : (https://dispendukcapil.Kabupaten Nganjuk.go.id/)\n\nNOTE : jika belum memiliki akun silahkan mendaftar akun dengan memilih menu ""DAFTAR""  jika sudah memiliki akun silahkan langsung login menggunakan username dan password kemudian memilih menu ""PERMOHONAN AKTA PERCERAIAN"".\n\n\n*Apakah Anda ingin mengajukan Permohonan melaui situs Disdukcapil Nganjuk?*\nJika YA ketik *YA* untuk mengajukan Permohonan.\nJika Tidak ketik *Tidak* Akhiri Percakapan.\nJika ingin kembali ke MENU UTAMA silakan ketik *Menu*', NULL),
